  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all crystals as @crystals" do
      crystal = Crystal.create! valid_attributes
      get :index
      assigns(:crystals).should eq([crystal])
    end
  end

  describe "GET show" do
    it "assigns the requested crystal as @crystal" do
      crystal = Crystal.create! valid_attributes
      get :show, :id => crystal.id
      assigns(:crystal).should eq(crystal)
