  end

  describe "GET new" do
    it "assigns a new crystal as @crystal" do
      get :new
      assigns(:crystal).should be_a_new(Crystal)
    end
  end

  describe "GET edit" do
    it "assigns the requested crystal as @crystal" do
      crystal = Crystal.create! valid_attributes
      get :edit, :id => crystal.id
      assigns(:crystal).should eq(crystal)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Crystal" do
        expect {
          post :create, :crystal => valid_attributes
        }.to change(Crystal, :count).by(1)
      end

      it "assigns a newly created crystal as @crystal" do
        post :create, :crystal => valid_attributes
        assigns(:crystal).should be_a(Crystal)
