        Crystal.any_instance.stub(:save).and_return(false)
        post :create, :crystal => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested crystal" do
        crystal = Crystal.create! valid_attributes
